mapper.select:
select uid , id , saved , name , url , type , extension , size , reg_id , reg_dt from k_spp.file where use_at = 'Y' and uid = #{uid} and saved != 'P'
and saved = #{saved}
and reg_id = #{regId}
order by reg_dt desc

mapper.insert:
insert into k_spp.file
    (
        uid, id, saved, name, url
      , type, extension, size, reg_id
    )
values
    (
        #{uid}, #{id} , #{saved}, #{name}, #{url}
      , #{type}, #{extension}, #{size}, #{regId}
    )

mapper.update:
        WITH UPSERT AS (
            UPDATE k_spp.file
               SET id = #{id}
				 , saved = #{saved}
		     WHERE uid = #{uid}
		       AND url = #{url}
		     RETURNING *
        )
        INSERT INTO k_spp.file (
            uid, id, saved, name, url
		  , type, extension, size, reg_id
		)
        SELECT #{uid}
             , #{id}
             , #{saved}
             , #{name}
             , #{url}
             , #{type}
             , #{extension}
             , #{size}
             , #{regId}
         WHERE NOT EXISTS ( SELECT * FROM UPSERT )

update k_spp.file
   set use_at = 'N'
 where url = #{url}

update k_spp.file
   set saved = 'Y'
     , uid = #{uid}
 where reg_id = #{regId}
   and url = #{url}

mapper.delete:
delete from k_spp.file
 where url = #{url}
   
   and saved = #{saved}

mapper.insert:
insert into k_spp.file_log
    (
        file_url
      , reg_id
    )
values
    (
        #{fileUrl}
      , #{regId}
    )

mapper.select:
select case when count(*) > 0 then 1 else 0 end
  from k_spp.file
 where uid = #{uid}
   and reg_id = #{regId}
